import { Pipe, PipeTransform } from '@angular/core'
import { intersection } from 'lodash'
import { Book } from 'models/book.model'

@Pipe({
  name: 'bookFilter',
})
export class BookFilterPipe implements PipeTransform {
  transform(books: Book[], filterMethod: any, ...args): any {
    if (!filterMethod || filterMethod === 'no filter') {
      return books
    }

    if (filterMethod === 'tags') {
      const tags = args[0]
      if (!tags) {
        return books
      }
      if (!tags.length) {
        return books
      }
      return books.filter(book => !!intersection(book.tags, tags).length)
    }

    return books.filter(book => book[filterMethod])
  }
}

import { Pipe, PipeTransform } from '@angular/core'
import { Book } from 'models/book.model'

@Pipe({
  name: 'bookGrouping',
})
export class BookGroupingPipe implements PipeTransform {
  transform(books: Book[], groupingMethod?: any): any {
    if (!groupingMethod || groupingMethod === 'no grouping') {
      return {}
    }
    const orderedItems = {}

    if (groupingMethod === 'genre') {
      books.forEach(book => {
        if (!book.genres) {
          book.genres = ['No genre']
        }
        book.genres.forEach(genre => {
          if (Object.keys(orderedItems).includes(genre)) {
            orderedItems[genre].push(book)
          } else {
            orderedItems[genre] = [book]
          }
        })
      })
      return orderedItems
    }

    books.forEach(book => {
      let order = book[groupingMethod]
      if (groupingMethod === 'rating') {
        order = `${book.rating || 0}`
      }
      if (groupingMethod === 'year') {
        order = `${book.year || 'Unknown year'}`
      }
      if (groupingMethod === 'title') {
        order = order[0].toLocaleUpperCase()
      }
      if (groupingMethod === 'date') {
        order = new Date(book.date).toLocaleDateString()
      }
      if (groupingMethod === 'language') {
        order = book.language || 'Unknown language'
      }

      if (Object.keys(orderedItems).includes(order)) {
        orderedItems[order].push(book)
      } else {
        orderedItems[order] = [book]
      }
    })

    return orderedItems
  }
}

import { Pipe, PipeTransform } from '@angular/core'
import { Collection } from 'models/collection.model'

@Pipe({
  name: 'collectionGrouping',
})
export class CollectionGroupingPipe implements PipeTransform {
  transform(collections: Collection[], groupingMethod?: any): any {
    const orderedCollections = Array.from(collections)
    if (!groupingMethod || groupingMethod === 'no grouping') {
      return orderedCollections
    }

    return orderedCollections.sort((a, b) => {
      let sortA, sortB
      if (groupingMethod === 'size') {
        sortA = a.books.length
        sortB = b.books.length
      } else {
        sortA = a[groupingMethod]
        sortB = b[groupingMethod]
      }
      if (sortA < sortB) {
        return -1
      }
      if (sortA > sortB) {
        return 1
      }
      return 0
    })
  }
}

import { Pipe, PipeTransform } from '@angular/core'

@Pipe({
  name: 'iterable',
})
export class IterableObjectPipe implements PipeTransform {
  transform(object: any): any {
    if (!object) {
      return null
    }
    return Object.keys(object)
      .map(key => {
        const id = `id${key
          .split(' ')
          .join('_')
          .split('/')
          .join('_')
          .toLowerCase()}`
        return { id, key, value: object[key] }
      })
      .sort((a, b) => {
        if (a.key < b.key) {
          return -1
        }
        if (a.key > b.key) {
          return 1
        }
        return 0
      })
  }
}

import { Pipe, PipeTransform } from '@angular/core'
import { Book } from 'database/models/book.model'

@Pipe({
  name: 'page',
})
export class PagePipe implements PipeTransform {
  transform(books: Book[], page?: number, maxBooks?: number): any {
    if (!page && !maxBooks) {
      return books
    }

    return books.slice((page - 1) * maxBooks, page * maxBooks)
  }
}

import { Pipe, PipeTransform } from '@angular/core'

@Pipe({
  name: 'pushToBottom'
})
export class PushToBottomPipe implements PipeTransform {
  transform(array: any[], itemToPush?: string): any {
    if (!itemToPush) { return array }
    const position = array.findIndex((item) => item.key === itemToPush)
    const element = array.splice(position, 1)[0]
    array.push(element)
    return array
  }
}

import { Pipe, PipeTransform } from '@angular/core'

@Pipe({
  name: 'searchFilter',
})
export class SearchFilterPipe implements PipeTransform {
  transform(items: any[], query?: string, keys?: string[]): any {
    if (!query) {
      return items
    }

    return items.filter(item => {
      const queryItems = query.split(' ').filter(it => !!it)
      if (keys) {
        return keys.some(key =>
          queryItems.some(
            queryItem =>
              item[key] &&
              item[key].toLowerCase().includes(queryItem.toLowerCase())
          )
        )
      }
      const lowerCaseItems = item
        .split(' ')
        .filter(it => !!it)
        .map(it => it.toLowerCase())
      return lowerCaseItems.some(it =>
        queryItems.some(queryItem => it.includes(queryItem.toLowerCase()))
      )
    })
  }
}

import { Pipe, PipeTransform } from '@angular/core'

const PARAGRAPH_SPLIT = '<br /><br />'

@Pipe({
  name: 'truncate',
})
export class TruncatePipe implements PipeTransform {
  transform(
    text: string,
    { length, paragraphs, paragraphStart, paragraphEnd }
  ): any {
    if (length) {
      return `${text.substr(0, length)}...`
    }

    if (paragraphs) {
      return text
        .split(PARAGRAPH_SPLIT)
        .slice(0, paragraphs)
        .join(PARAGRAPH_SPLIT)
    }

    if (paragraphStart || paragraphEnd) {
      return text
        .split(PARAGRAPH_SPLIT)
        .slice(paragraphStart, paragraphEnd)
        .join(PARAGRAPH_SPLIT)
    }
  }
}

