import { Injectable } from '@angular/core'
import { AngularFireAuth } from 'angularfire2/auth'
import { Router } from '@angular/router'
import { BehaviorSubject } from 'rxjs'
import * as firebase from 'firebase/app'
import * as auth0 from 'auth0-js'
import { DatabaseService } from './database.service'
import { SessionService } from './session.service'
import { User as LocalUser } from '../models/user.model'
import { User as DBUser } from '../database/models/user.model'
import { environment } from 'environments/environment'

@Injectable()
export class AuthService {
  private readonly localDomain = environment.localDomain
  private readonly clientID = environment.auth0Config.clientId
  private readonly domain = environment.auth0Config.domain
  private readonly redirectURI = environment.auth0Config.redirectUri
  private readonly defaultParams = {
    clientID: this.clientID,
    domain: this.domain,
    responseType: 'token id_token',
    audience: `https://${this.domain}/userinfo`,
    redirectUri: this.redirectURI,
    scope: 'openid',
  }

  private auth0 = new auth0.WebAuth(this.defaultParams)

  private _userRef = new BehaviorSubject<string>(undefined)
  private _goodreadsId = new BehaviorSubject<number>(undefined)
  private _userLoaded = new BehaviorSubject<boolean>(false)

  userRef = this._userRef.asObservable()
  goodreadsId = this._goodreadsId.asObservable()
  userLoaded = this._userLoaded.asObservable()

  constructor(
    public fireAuth: AngularFireAuth,
    private router: Router,
    private database: DatabaseService,
    private session: SessionService
  ) {
    const user = this.session.localUser
    if (user) {
      this._userRef.next(user.id)
      this._goodreadsId.next(user.goodreadsId)
    }
    this.setupSessionForGoodreadsLogin()
    this.setupFirebaseSessionListener()
  }

  private setupSessionForGoodreadsLogin() {
    this.auth0.parseHash((error, result) => {
      if (result && result.accessToken && result.idToken) {
        window.location.hash = ''

        this.auth0.client.userInfo(result.accessToken, (err, user) => {
          const goodreadsId = user.sub.split('|')[2]
          this.session.goodreadsId = goodreadsId
          this.database.updateUser(this.session.userId, {
            goodreadsId,
          })
        })
      } else if (error) {
        console.log('Could not log in on Goodreads')
        console.log(error)
      }
    })
  }

  private createUserInDatabase(user, params: object = {}) {
    return this.database
      .findUserByParam('uid', user.uid)
      .then(async userInDatabase => {
        if (userInDatabase === null) {
          console.log('User not found in database')
          console.log('Creating user in database')

          const newUser = {
            uid: user.uid,
            name: user.displayName || params['displayName'],
            email: user.email,
            displayWelcomeMessage: true,
            avatarUrl: user.photoUrl || user.photoURL,
          } as DBUser

          return this.database.createUser(newUser)
        } else {
          return userInDatabase as LocalUser
        }
      })
  }

  private processResponse(user: object, params: object = {}) {
    return this.createUserInDatabase(user, params).then(userInDatabase => {
      localStorage.setItem('userLoginCredentials', JSON.stringify(user))
      this.database.isLoggedIn.next(true)
      this.session.buildSession(userInDatabase)
      this.router.navigate(['library'])
    })
  }

  private setupFirebaseSessionListener() {
    this.fireAuth.auth.onAuthStateChanged(user => {
      if (user) {
        this._userLoaded.next(true)
      } else {
        this._userLoaded.next(false)
      }
    })
  }

  loginGoodreads(redirectPath?: string) {
    const redirectUri = redirectPath
      ? `${this.localDomain}/${redirectPath}`
      : this.redirectURI

    this.auth0 = new auth0.WebAuth({
      ...this.defaultParams,
      redirectUri,
      owp: true,
    })

    this.auth0.authorize({ connection: 'goodreads' }, () => {})
  }

  loginGoogle() {
    this.fireAuth.auth
      .signInWithPopup(new firebase.auth.GoogleAuthProvider())
      .then(response => {
        this.processResponse(response.user)
      })
      .catch(error => {
        console.log('Could not login with Google')
        console.log(error)
      })
  }

  loginFacebook() {
    this.fireAuth.auth
      .signInWithPopup(new firebase.auth.FacebookAuthProvider())
      .then(response => {
        this.processResponse(response.user)
      })
      .catch(error => {
        console.log('Could not login with Facebook')
        console.log(error)
      })
  }

  loginEmail({ email, password, name }, onError = (...args) => {}) {
    return this.fireAuth.auth
      .signInWithEmailAndPassword(email, password)
      .then(response =>
        this.processResponse(response.user, { displayName: name })
      )
      .catch(error => {
        console.log('Could not login with e-mail and password')
        console.log(error.code, error.message)
        onError(error)
      })
  }

  signUpWithEmail({ email, password, name }) {
    this.fireAuth.auth
      .createUserWithEmailAndPassword(email, password)
      .then(response => {
        this.processResponse(response.user, { displayName: name })
      })
      .then(() => {
        this.sendVerificationEmail()
      })
      .catch(error => {
        console.log('Could not sign up with e-mail and password')
        console.log(error.code, error.message)
      })
  }

  logout() {
    this.fireAuth.auth
      .signOut()
      .then(() => {
        console.log('Sucessefully signed out')

        localStorage.removeItem('userLoginCredentials')
        this.database.isLoggedIn.next(false)
        this.session.destroySession()
        this.router.navigate(['home'])
      })
      .catch(error => {
        console.log('Could not sign out')
        console.log(error.code, error.message)
      })
  }

  resetPassword() {}

  deleteAccount() {
    Promise.all([
      this.database.deleteAllBooksForUser(this.session.userId),
      this.database.deleteAllCollectionsForUser(this.session.userId),
      this.database.deleteUser(this.session.userId),
    ])
      .then(() => this.fireAuth.auth.currentUser.delete())
      .then(() => console.log('Successfully deleted user'))
      .then(() => this.logout())
  }

  get userIsVerified() {
    return (
      this.fireAuth.auth.currentUser &&
      this.fireAuth.auth.currentUser.emailVerified
    )
  }

  sendVerificationEmail() {
    return this.fireAuth.auth.currentUser.sendEmailVerification()
  }

  reauthenticateUser(password: string) {
    const credentials = firebase.auth.EmailAuthProvider.credential(
      this.fireAuth.auth.currentUser.email,
      password
    )
    return this.fireAuth.auth.currentUser.reauthenticateWithCredential(
      credentials
    )
  }

  updateUserEmail(email: string, password: string) {
    return this.reauthenticateUser(password)
      .then(() => this.fireAuth.auth.currentUser.updateEmail(email))
      .then(() => this.sendVerificationEmail())
      .then(() => this.database.updateUser(this.session.userId, { email }))
  }

  updateUserPassword(oldPassword: string, password: string) {
    return this.reauthenticateUser(oldPassword).then(() =>
      this.fireAuth.auth.currentUser.updatePassword(password)
    )
  }

  signInProvider() {
    return this.fireAuth.auth.currentUser.providerData[0].providerId
  }
}

import { Injectable } from '@angular/core'
import {
  AngularFireDatabase,
  AngularFireList,
  AngularFireObject,
} from 'angularfire2/database'
import { AngularFireStorage } from 'angularfire2/storage'
import { difference, intersection } from 'lodash'
import { User } from '../database/models/user.model'
import { Book } from '../database/models/book.model'
import { Collection } from '../database/models/collection.model'
import { objectToArray, findKeyByValue, unique } from '../utils/helpers'
import { environment } from 'environments/environment'
import { BehaviorSubject, Observable } from 'rxjs'
import { map, mergeMap, last } from 'rxjs/operators'
import { SessionService } from './session.service'
@Injectable()
export class DatabaseService {
  users: AngularFireList<User>
  books: AngularFireList<Book>
  collections: AngularFireList<Collection>
  isLoggedIn = new BehaviorSubject<boolean>(false)

  isLoggedIn$ = this.isLoggedIn.asObservable()

  constructor(
    private db: AngularFireDatabase,
    private session: SessionService,
    private storage: AngularFireStorage
  ) {
    this.books = db.list(`${environment.rootUrl}/books`)
    this.users = db.list(`${environment.rootUrl}/users`)
    this.collections = db.list(`${environment.rootUrl}/collections`)
  }

  private userBooksRef(userRef: string): AngularFireList<string> {
    return this.db.list(`${environment.rootUrl}/users/${userRef}/books`)
  }

  private userCollectionsRef(userRef: string): AngularFireList<string> {
    return this.db.list(`${environment.rootUrl}/users/${userRef}/collections`)
  }

  private userTagsRef(userRef: string): AngularFireObject<string> {
    return this.db.object(`${environment.rootUrl}/users/${userRef}/tags`)
  }

  private userGenresRef(userRef: string): AngularFireObject<string> {
    return this.db.object(`${environment.rootUrl}/users/${userRef}/genres`)
  }

  private findById(
    id: string,
    model: AngularFireList<any>,
    parseFn: (object, id) => {}
  ) {
    return model.query
      .orderByKey()
      .equalTo(id)
      .once('value')
      .then(snap =>
        snap.val()
          ? parseFn(
              snap.val()[Object.keys(snap.val())[0]],
              Object.keys(snap.val())[0]
            )
          : null
      )
  }

  private findByParam(
    key: string,
    value: string,
    model: AngularFireList<any>,
    parseFn: (object, id) => {}
  ) {
    return model.query
      .orderByChild(key)
      .equalTo(value)
      .once('value')
      .then(snap =>
        snap.val()
          ? parseFn(
              snap.val()[Object.keys(snap.val())[0]],
              Object.keys(snap.val())[0]
            )
          : null
      )
  }

  private getByParams(
    key: string,
    value: string,
    model: AngularFireList<any>,
    parseFn: (object, id) => {}
  ) {
    return model.query
      .orderByChild(key)
      .equalTo(value)
      .once('value')
      .then(snap =>
        snap.val()
          ? Object.keys(snap.val()).map(_key => parseFn(snap.val()[_key], _key))
          : []
      )
  }

  private subscribeTo(
    userRef: string,
    model: string,
    child: string = 'ownerId'
  ) {
    return this.db.list(`${environment.rootUrl}/${model}`, ref =>
      ref.orderByChild(child).equalTo(userRef)
    )
  }

  private removeFromUser(collection: AngularFireList<string>, id: string) {
    return collection.query.once('value').then(res => {
      const ids = res.val()
      const ref = findKeyByValue(ids, id)
      return collection.remove(ref)
    })
  }

  /* USER */
  private parseUser(user: User, id: string) {
    return {
      ...user,
      id,
      books: objectToArray(user.books) || [],
      collections: objectToArray(user.collections) || [],
    }
  }

  /* BOOK */
  private parseBook(book: Book, id: string): Book {
    return {
      ...book,
      id,
      genres: book.genres || [],
      collections: book.collections || [],
      tags: book.tags || [],
    }
  }

  private createBook(book: Book) {
    return this.books
      .push(book)
      .then(res => this.parseBook(book, res.ref.key)) as Promise<Book>
  }

  private getBooksByParam(key: string, value: string) {
    return this.getByParams(key, value, this.books, this.parseBook) as Promise<
      Book[]
    >
  }

  private updateBook(book: Book) {
    return this.books.update(book.id, book).then(() => book)
  }

  private deleteBook(book: Book) {
    return this.books.remove(book.id).then(() => book)
  }

  private removeBookFromUser(userRef: string, id: string) {
    return this.removeFromUser(this.userBooksRef(userRef), id)
  }

  /* COLLECTION */
  private parseCollection(collection: Collection, id: string) {
    return {
      ...collection,
      id,
      books: collection.books || [],
    }
  }

  private createCollection(collection: Collection) {
    return this.collections
      .push(collection)
      .then(res => this.parseCollection(collection, res.ref.key)) as Promise<
      Collection
    >
  }

  private getCollectionsByParam(key: string, value: string) {
    return this.getByParams(
      key,
      value,
      this.collections,
      this.parseCollection
    ) as Promise<Collection[]>
  }

  private updateCollection(collection: Collection) {
    return this.collections
      .update(collection.id, collection)
      .then(() => collection)
  }

  private deleteCollection(collection: Collection) {
    return this.collections.remove(collection.id).then(() => collection)
  }

  private removeCollectionFromUser(userRef: string, id: string) {
    return this.removeFromUser(this.userCollectionsRef(userRef), id)
  }

  private userTagRef(userRef: string, tag: string) {
    return this.db.object(`${environment.rootUrl}/users/${userRef}/tags/${tag}`)
  }

  private findTagCount(userRef: string, tag: string) {
    return this.userTagRef(userRef, tag)
      .query.once('value')
      .then(snap => snap.val())
  }

  private getTagsForUser(userRef: string) {
    return this.userTagsRef(userRef)
      .query.once('value')
      .then(snap => snap.val() || {})
  }

  private userGenreRef(userRef: string, genre: string) {
    return this.db.object(
      `${environment.rootUrl}/users/${userRef}/genres/${genre}`
    )
  }

  private findGenreCount(userRef: string, genre: string) {
    return this.userGenreRef(userRef, genre)
      .query.once('value')
      .then(snap => snap.val())
  }

  private getGenresForUser(userRef: string) {
    return this.userGenresRef(userRef)
      .query.once('value')
      .then(snap => snap.val() || {})
  }

  private mergeTags(
    userRef: string,
    tagsToAdd: string[] = [],
    tagsToRemove: string[] = []
  ) {
    return this.getTagsForUser(userRef).then(userTags => {
      const newTags = difference(tagsToAdd, Object.keys(userTags))
      const existingTags = intersection(tagsToAdd, Object.keys(userTags))

      newTags.forEach(tag => this.userTagRef(userRef, tag).set(1))
      existingTags.forEach(tag =>
        this.findTagCount(userRef, tag).then(tagCount =>
          this.userTagRef(userRef, tag).set(tagCount + 1)
        )
      )
      tagsToRemove.forEach(tag => {
        this.findTagCount(userRef, tag).then(tagCount => {
          if (tagCount === 1) {
            this.userTagRef(userRef, tag).remove()
          } else {
            this.userTagRef(userRef, tag).set(tagCount - 1)
          }
        })
      })
    })
  }

  private mergeGenres(
    userRef: string,
    genresToAdd: string[] = [],
    genresToRemove: string[] = []
  ) {
    return this.getGenresForUser(userRef).then(userGenres => {
      const newGenres = difference(genresToAdd, Object.keys(userGenres))
      const existingGenres = intersection(genresToAdd, Object.keys(userGenres))

      newGenres.forEach(genre => this.userGenreRef(userRef, genre).set(1))
      existingGenres.forEach(genre =>
        this.findGenreCount(userRef, genre).then(genreCount =>
          this.userGenreRef(userRef, genre).set(genreCount + 1)
        )
      )
      genresToRemove.forEach(genre => {
        this.findGenreCount(userRef, genre).then(genreCount => {
          if (genreCount === 1) {
            this.userGenreRef(userRef, genre).remove()
          } else {
            this.userGenreRef(userRef, genre).set(genreCount - 1)
          }
        })
      })
    })
  }

  private uploadFile(file, filePath: string) {
    const path = this.storage.ref(filePath)
    const task = path.put(file)

    return task.snapshotChanges().pipe(
      last(),
      mergeMap(() => path.getDownloadURL())
    )
  }

  createUser(user: User) {
    return this.users.push(user).then(res => this.parseUser(user, res.ref.key))
  }

  findUserById(id: string) {
    return this.findById(id, this.users, this.parseUser).then(
      user => user as User
    )
  }

  findUserByParam(key: string, value: string) {
    return this.findByParam(key, value, this.users, this.parseUser).then(
      user => user as User
    )
  }

  updateUser(id: string, params: object) {
    this.users.update(id, params as User)
    return this.findUserById(id).then(user => (this.session.localUser = user))
  }

  deleteUser(id: string) {
    return this.users.remove(id)
  }

  subscribeToBooksFromUser(userRef: string) {
    return this.subscribeTo(userRef, 'books').valueChanges() as Observable<
      Book[]
    >
  }

  subscribeToLatestBooks(userRef: string, limit: number) {
    return this.db
      .list(`${environment.rootUrl}/books`, ref =>
        ref
          .orderByChild('ownerId')
          .equalTo(userRef)
          .limitToLast(limit)
      )
      .valueChanges() as Observable<Book[]>
  }

  subscribeToBookCount(userRef: string) {
    return this.userBooksRef(userRef)
      .valueChanges()
      .pipe(map(bookIds => bookIds.length)) as Observable<number>
  }

  subscribeToCollectionCount(userRef: string) {
    return this.userCollectionsRef(userRef)
      .valueChanges()
      .pipe(map(collectionIds => collectionIds.length)) as Observable<number>
  }

  createBookForUser(userRef: string, book) {
    return this.createBook({ ...book, ownerId: userRef }).then(
      bookInDatabase => {
        return Promise.all([
          this.userBooksRef(userRef).push(bookInDatabase.id),
          this.mergeGenres(userRef, book.genres, []),
          this.mergeTags(userRef, book.tags, []),
          ...book.collections.map(collectionId => {
            return this.addBooksToCollection(collectionId, [bookInDatabase.id])
          }),
        ])
          .then(() => this.updateBook(bookInDatabase))
          .then(() => bookInDatabase)
      }
    )
  }

  findBookById(id: string) {
    return this.findById(id, this.books, this.parseBook).then(
      book => book as Book
    )
  }

  getBooksForUser(userRef: string) {
    return this.getBooksByParam('ownerId', userRef)
  }

  getBooksForCollection(id: string, userRef: string) {
    return this.getBooksForUser(userRef).then(books =>
      books.filter(book => book.collections.includes(id))
    )
  }

  updateBookForUser(userRef: string, book: Book) {
    return this.findBookById(book.id).then(oldBook => {
      const oldCollections = oldBook.collections || []
      const newCollections = book.collections || []

      const oldTags = oldBook.tags || []
      const newTags = book.tags || []

      const oldGenres = oldBook.genres || []
      const newGenres = book.genres || []

      const collectionsToAdd = newCollections.filter(
        collection => !oldCollections.includes(collection)
      )
      const collectionsToRemove = oldCollections.filter(
        collection => !newCollections.includes(collection)
      )

      const tagsToAdd = difference(newTags, oldTags)
      const tagsToRemove = difference(oldTags, newTags)

      const genresToAdd = difference(newGenres, oldGenres)
      const genresToRemove = difference(oldGenres, newGenres)

      return Promise.all([
        this.updateBook({ ...oldBook, ...book }),
        this.mergeGenres(userRef, genresToAdd, genresToRemove),
        this.mergeTags(userRef, tagsToAdd, tagsToRemove),
        this.addBooksToCollections(collectionsToAdd, [book.id]),
        this.removeBooksFromCollections(collectionsToRemove, [book.id]),
      ]).then(() => book)
    })
  }

  deleteBookForUser(userRef: string, book: Book) {
    return Promise.all([
      this.deleteBook(book),
      this.mergeGenres(userRef, [], book.genres),
      this.mergeTags(userRef, [], book.tags),
      this.removeBookFromUser(userRef, book.id),
      this.removeBooksFromCollections(book.collections, [book.id]),
    ])
  }

  deleteAllBooksForUser(userRef: string) {
    return Promise.resolve()
      .then(() => this.getBooksForUser(userRef))
      .then(books => Promise.all(books.map(book => this.deleteBook(book))))
  }

  subscribeToCollectionsFromUser(userRef: string) {
    return this.subscribeTo(
      userRef,
      'collections'
    ).valueChanges() as Observable<Collection[]>
  }

  createCollectionForUser(userRef: string, collection) {
    return this.createCollection({ ...collection, ownerId: userRef }).then(
      collectionInDatabase => {
        return Promise.all(
          collectionInDatabase.books.map(bookId =>
            this.addCollectionsToBook(bookId, [collectionInDatabase.id])
          )
        )
          .then(() =>
            this.userCollectionsRef(userRef).push(collectionInDatabase.id)
          )
          .then(() => this.updateCollection(collectionInDatabase))
          .then(() => collectionInDatabase)
      }
    )
  }

  findCollectionById(id: string) {
    return this.findById(id, this.collections, this.parseCollection).then(
      collection => collection as Collection
    )
  }

  getCollectionsForUser(userRef: string) {
    return this.getCollectionsByParam('ownerId', userRef)
  }

  updateCollectionForUser(userRef: string, collection: Collection) {
    return this.findCollectionById(collection.id).then(oldCollection => {
      const oldBooks = oldCollection.books || []
      const newBooks = collection.books || []

      const booksToAdd = newBooks.filter(book => !oldBooks.includes(book))
      const booksToRemove = oldBooks.filter(book => !newBooks.includes(book))

      return Promise.all([
        this.updateCollection({ ...oldCollection, ...collection }),
        this.addCollectionsToBooks(booksToAdd, [collection.id]),
        this.removeCollectionsFromBooks(booksToRemove, [collection.id]),
      ])
    })
  }

  deleteCollectionForUser(userRef: string, collection: Collection) {
    return Promise.all([
      this.deleteCollection(collection),
      this.removeCollectionFromUser(userRef, collection.id),
      this.removeCollectionsFromBooks(collection.books, [collection.id]),
    ])
  }

  deleteAllCollectionsForUser(userRef: string) {
    return Promise.resolve()
      .then(() => this.getCollectionsForUser(userRef))
      .then(collections =>
        Promise.all(
          collections.map(collection => this.deleteCollection(collection))
        )
      )
  }

  addBooksToCollection(collectionId: string, bookIds: string[]) {
    return this.findCollectionById(collectionId).then(collection => {
      const booksForCollection = [...bookIds, ...collection.books]
      return this.collections
        .update(collectionId, {
          books: unique(booksForCollection),
        } as Collection)
        .then(() => booksForCollection)
    })
  }

  addBooksToCollections(collectionIds: string[], bookIds: string[]) {
    return Promise.all([
      ...collectionIds.map(id => this.addBooksToCollection(id, bookIds)),
    ])
  }

  removeBooksFromCollection(collectionId: string, bookIds: string[]) {
    return this.findCollectionById(collectionId).then(collection => {
      const booksForCollection = collection.books.filter(
        bookId => !bookIds.includes(bookId)
      )
      return this.collections
        .update(collectionId, {
          books: booksForCollection,
        } as Collection)
        .then(() => bookIds)
    })
  }

  removeBooksFromCollections(collectionsIds: string[], bookIds: string[]) {
    return Promise.all([
      ...collectionsIds.map(collectionId =>
        this.removeBooksFromCollection(collectionId, bookIds)
      ),
    ])
  }

  addCollectionsToBook(id: string, collectionIds: string[]) {
    return this.findBookById(id).then(book => {
      const collectionsForBook = [...collectionIds, ...book.collections]
      return this.books
        .update(id, {
          collections: unique(collectionsForBook),
        } as Book)
        .then(() => collectionsForBook)
    })
  }

  addCollectionsToBooks(bookIds: string[], collectionIds: string[]) {
    return Promise.all([
      ...bookIds.map(id => this.addCollectionsToBook(id, collectionIds)),
    ])
  }

  removeCollectionsFromBook(id: string, collectionIds: string[]) {
    return this.findBookById(id).then(book => {
      const collectionsForBook = book.collections.filter(
        collectionId => !collectionIds.includes(collectionId)
      )
      return this.books
        .update(id, {
          collections: collectionsForBook,
        } as Book)
        .then(() => collectionIds)
    })
  }

  removeCollectionsFromBooks(bookIds: string[], collectionIds: string[]) {
    return Promise.all([
      ...bookIds.map(bookId =>
        this.removeCollectionsFromBook(bookId, collectionIds)
      ),
    ])
  }

  subscribeToTagsFromUser(userRef: string) {
    return this.userTagsRef(userRef)
      .valueChanges()
      .pipe(map(value => (value ? Object.keys(value) : null))) as Observable<
      string[]
    >
  }

  subscribeToGenresFromUser(userRef: string) {
    return this.userGenresRef(userRef)
      .valueChanges()
      .pipe(map(value => (value ? Object.keys(value) : null))) as Observable<
      string[]
    >
  }

  uploadBookCover(userRef: string, bookId: string, file) {
    return this.uploadFile(file, `images/${userRef}/${bookId}.jpg`)
  }

  uploadAvatar(userRef: string, file) {
    return this.uploadFile(file, `images/${userRef}/avatar.jpg`)
  }

  uploadBackgroundImage(userRef: string, file) {
    return this.uploadFile(file, `images/${userRef}/background.jpg`)
  }

  cleanTestBed() {
    console.log('Cleaning test bed')
    if (environment.name !== 'test') {
      return
    }

    this.books.remove()
    this.users.remove()
    this.collections.remove()
  }
}

import { Injectable } from '@angular/core'
import { HttpClient, HttpParams } from '@angular/common/http'
import { HttpGet, HttpGetAll } from 'utils/http'
import { environment } from 'environments/environment'
import { SessionService } from './session.service'
import * as _ from 'lodash'
import { BehaviorSubject, Observable } from 'rxjs'
import { map, mergeMap } from 'rxjs/operators'
import { Book } from 'models/book.model'

const USE_PROXY = true

@Injectable()
export class GoodreadsService {
  private key = environment.goodreadsConfig.key
  private domain = USE_PROXY
    ? environment.goodreadsConfig.proxyDomain
    : environment.goodreadsConfig.domain

  private id = new BehaviorSubject<number>(undefined)

  goodreadsId = this.id.asObservable()
  defaultParams = new HttpParams().set('key', this.key)

  constructor(
    private http: HttpClient,
    private sessionService: SessionService
  ) {
    if (this.sessionService.localUser) {
      this.id.next(this.sessionService.localUser.goodreadsId)
    }
  }

  private parseUrl(url: string) {
    return `${url}.xml`
  }

  getUser(id?: number) {
    const userId = id ? id : this.id
    const url = `${this.domain}/user/show/${userId}`

    return HttpGet(this.http, this.parseUrl(url), this.defaultParams)
  }

  getBook(id: number) {
    // key: Developer key (required).
    // id: A Goodreads internal book_id
    // text_only: Only show reviews that have text (default false)
    // rating: Show only reviews with a particular rating (optional)
    const url = `${this.domain}/book/show/${id}`

    return HttpGet(this.http, this.parseUrl(url), this.defaultParams).pipe(
      map<any, any>(response => response.book)
    )
  }

  getAuthor(id: number) {
    const url = `${this.domain}/author/show/${id}`

    return HttpGet(this.http, this.parseUrl(url), this.defaultParams).pipe(
      map<any, any>(response => response.author)
    )
  }

  getBooks(ids: number[]) {
    const requests = ids.map(id => ({
      url: this.parseUrl(`${this.domain}/book/show/${id}`),
      params: this.defaultParams,
    }))
    return HttpGetAll(this.http, requests).pipe(
      map(responses => responses.map((response: any) => response.book))
    )
  }

  getAuthors(ids: number[]) {
    return HttpGetAll(
      this.http,
      ids.map(id => ({
        url: `${this.domain}/author/show/${id}`,
        params: this.defaultParams,
      }))
    ).pipe(map<any, any>(results => results.map(result => result.author)))
  }

  getBooksForUser(id: number) {
    const params = this.defaultParams
      .set('v', '2')
      .set('id', `${id}`)
      .set('shelf', 'all')
      .set('sort', 'title')
      .set('format', 'xml')
    const url = `${this.domain}/review/list`

    return HttpGet(this.http, url, params).pipe(
      map<any, any>(response =>
        response ? response.reviews.review.map(review => review.book) : response
      )
    ) as Observable<Book[]>
  }

  searchBook(query: string) {
    // q: The query text to match against book title, author, and ISBN fields. Supports boolean operators and phrase searching.
    // page: Which page to return (default 1, optional)
    // key: Developer key (required).
    // search[field]: Field to search, one of 'title', 'author', or 'all' (default is 'all')
    const url = `${this.domain}/search/index`
    const params = this.defaultParams.set('q', decodeURI(query))

    return HttpGet(this.http, this.parseUrl(url), params).pipe(
      map<any, any>(response => {
        const work = response.search.results.work
        const results = Array.isArray(work) ? work : [work]
        return _.uniq(results.map(item => item.best_book))
      })
    )
  }

  searchAuthor(name: string) {
    const url = `${this.domain}/search/index`
    const params = this.defaultParams
      .set('q', decodeURI(name))
      .set('search[field]', 'author')

    return HttpGet(this.http, this.parseUrl(url), params).pipe(
      map<any, any>(response =>
        _.uniq(
          response.search.results.work.map(item => item.best_book.author.id._)
        )
      ),
      mergeMap<any, any>(authorIds => this.getAuthors(authorIds))
    )
  }
}

import { Injectable } from '@angular/core'
import * as XLSX from 'xlsx'
import { LANGUAGES } from 'utils/constants'
import { upperCaseFirstLetter } from 'utils/helpers'
import { Book } from 'database/models/book.model'
import { BehaviorSubject } from 'rxjs'
import { notify } from 'utils/notifications'

type AOA = any[][]

@Injectable()
export class ImportService {
  private booksToImport = new BehaviorSubject<Book[]>([])

  booksToImport$ = this.booksToImport.asObservable()

  readFile(uploadedFile) {
    const reader = new FileReader()

    reader.onload = (e: any) => {
      const binaryString = e.target.result
      const workBook: XLSX.WorkBook = XLSX.read(binaryString, {
        type: 'binary',
      })

      const workSheet: XLSX.WorkSheet = workBook.Sheets[workBook.SheetNames[0]]

      const file = XLSX.utils.sheet_to_json(workSheet, { header: 1 }) as AOA
      this.booksToImport.next(this.parseFile(file))
    }

    reader.readAsBinaryString(uploadedFile)
    notify({ message: 'Successfully read file' })
    return this.booksToImport$
  }

  parseFile(file: AOA) {
    return file
      .map(row => ({
        title: row[0],
        author: row[1],
        original: row[2],
        publisher: row[3],
        date: new Date().toISOString(),
        ...(row[4] ? { pages: +row[4] } : {}),
        ...(row[5] ? { year: +row[5] } : {}),
        ...(row[6] && LANGUAGES.includes(upperCaseFirstLetter(row[6]))
          ? { language: row[6] }
          : {}),
        ...(row[7] ? { rating: +row[7] } : {}),
        owned: !!row[8] && row[8].toLocaleLowerCase() === 'x',
        wishlist: !!row[9] && row[9].toLocaleLowerCase() === 'x',
        read: !!row[10] && row[10].toLocaleLowerCase() === 'x',
        favorite: !!row[11] && row[11].toLocaleLowerCase() === 'x',
        ...(row[12] ? { imageLarge: row[12] } : {}),
      }))
      .filter(book => !!book.title && !!book.author) as Book[]
  }
}

import { Injectable } from '@angular/core'
import { BehaviorSubject, Observable, of } from 'rxjs'
import { DatabaseService } from 'services/database.service'
import { Collection as RawCollection } from 'database/models/collection.model'
import { Book as RawBook } from 'database/models/book.model'
import { Book } from 'models/book.model'
import { Collection } from 'models/collection.model'
import { map, mergeMap, filter } from 'rxjs/operators'
import { omit, compact, uniq } from 'lodash'
import { SessionService } from './session.service'
import { Author } from 'models/author.model'
import { GoodreadsService } from './goodreads.service'
import { parseAuthor } from 'utils/helpers'
import { notify } from 'utils/notifications'
import { ignoreReturnFor } from 'utils/promise'

@Injectable()
export class LibraryService {
  set userRef(ref) {
    this._userRef = ref
  }
  set setBooksToImport(books: Book[]) {
    this.booksToImport.next(books)
  }
  private MAX_DATE = 4
  private collections = new BehaviorSubject<Collection[]>(undefined)
  private booksToImport = new BehaviorSubject<Book[]>(undefined)

  private _userRef: string

  books$: Observable<Book[]>
  latestBooks$: Observable<Book[]>
  collections$: Observable<Collection[]>
  authors$: Observable<Author[]>
  grAuthorIds$: Observable<number[]>
  tags$: Observable<string[]>
  genres$: Observable<string[]>
  booksToImport$ = this.booksToImport.asObservable()
  bookCount$: Observable<number>
  collectionCount$: Observable<number>
  authorCount$: Observable<number>

  rawBooks$: Observable<RawBook[]>
  rawCollections$: Observable<RawCollection[]>

  constructor(
    private database: DatabaseService,
    private goodreadsService: GoodreadsService,
    private session: SessionService
  ) {
    this.session.userId$.subscribe(
      userRefFromSession => (this._userRef = userRefFromSession)
    )
    this.loadLibrary()
  }

  private getBooksForCollection(collectionId: string = '', books: Book[] = []) {
    return books.filter(
      book => book.collections && book.collections.includes(collectionId)
    )
  }

  mapCollectionTitleToId(book: Book, collections: Collection[]) {
    if (!book.collections || !collections) {
      return []
    }

    return book.collections.map(
      collectionTitle =>
        collections.find(collection => collection.title === collectionTitle).id
    )
  }

  mapCollectionIdToTitle(
    collectionIds: string[] = [],
    collections: RawCollection[] = []
  ) {
    return collectionIds.map(
      collection => collections.find(c => c.id === collection).title
    )
  }

  loadLibrary() {
    console.log('Loading library...')
    this.rawBooks$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef => {
        return this.database.subscribeToBooksFromUser(userRef)
      }),
      map(books => {
        return books.map(book => ({
          ...book,
          collections: book.collections || [],
        }))
      })
    )
    this.rawCollections$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef => this.database.subscribeToCollectionsFromUser(userRef))
    )
    this.tags$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef => this.database.subscribeToTagsFromUser(userRef))
    )
    this.genres$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef => this.database.subscribeToGenresFromUser(userRef))
    )

    this.books$ = this.rawBooks$

    this.latestBooks$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef =>
        this.database.subscribeToLatestBooks(userRef, this.MAX_DATE)
      )
    )

    this.collections$ = this.rawBooks$.pipe(
      mergeMap(_books =>
        this.rawCollections$.pipe(
          map(_collections =>
            _collections.map(collection => ({
              ...collection,
              books: this.getBooksForCollection(collection.id, _books),
            }))
          )
        )
      )
    )
    this.collections$.subscribe(this.collections)

    this.grAuthorIds$ = this.rawBooks$.pipe(
      map(books => uniq(compact(books.map(book => book.goodreadsAuthorId))))
    )

    this.authors$ = this.grAuthorIds$.pipe(
      mergeMap(ids =>
        ids.length ? this.goodreadsService.getAuthors(ids) : of([])
      ),
      map(authors => authors.map(author => parseAuthor(author)))
    )

    this.bookCount$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef => this.database.subscribeToBookCount(userRef))
    )

    this.collectionCount$ = this.session.userId$.pipe(
      filter(userRef => !!userRef),
      mergeMap(userRef => this.database.subscribeToCollectionCount(userRef))
    )

    this.authorCount$ = this.authors$.pipe(map(authors => authors.length))
  }

  addBook(book: Book) {
    book.collections = this.mapCollectionTitleToId(book, this.collections.value)
    return this.database.createBookForUser(this._userRef, book).then(
      ignoreReturnFor(() =>
        notify({
          message: `<strong>${book.title}</strong> succesfully added`,
        })
      )
    )
  }

  addBooks(books: Book[]) {
    return Promise.all(books.map(book => this.addBook(book)))
  }

  findBook(id: string) {
    return this.rawCollections$.pipe(
      mergeMap(collections =>
        this.books$.pipe(
          map(books => {
            const book = books.find(b => b.id === id)
            if (!book) {
              return book
            }
            book.collections = this.mapCollectionIdToTitle(
              book.collections,
              collections
            )
            return book
          })
        )
      )
    )
  }

  updateBook(book, mapCollections = true) {
    return this.database
      .updateBookForUser(this._userRef, {
        ...book,
        collections: mapCollections
          ? this.mapCollectionTitleToId(book, this.collections.value)
          : book.collections,
      })
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `<strong>${book.title}</strong> succesfully updated`,
          })
        )
      )
      .catch(
        ignoreReturnFor(() =>
          notify({
            message: `<strong>${book.title}</strong> could not be updated`,
          })
        )
      )
  }

  deleteBook(book: Book) {
    return this.database
      .deleteBookForUser(this._userRef, {
        ...omit(book, ['isSelected']),
        collections: this.mapCollectionTitleToId(book, this.collections.value),
      })
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `<strong>${book.title}</strong> succesfully deleted`,
          })
        )
      )
  }

  getLatestBooks() {
    return this.database.subscribeToLatestBooks(this._userRef, this.MAX_DATE)
  }

  addCollection(collection: Collection) {
    return this.database
      .createCollectionForUser(this._userRef, {
        ...collection,
        books: collection.books.map(book => book.id),
      })
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `<strong>${collection.title}</strong> succesfully added`,
          })
        )
      )
  }

  findCollection(id: string) {
    return this.collections$.pipe(
      map(collections => collections.find(collection => collection.id === id))
    )
  }

  updateCollection(collection: Collection) {
    return this.database
      .updateCollectionForUser(this._userRef, {
        ...collection,
        books: collection.books.map(book => book.id),
      })
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `<strong>${collection.title}</strong> succesfully updated`,
          })
        )
      )
  }

  deleteCollection(collection: Collection) {
    return this.database
      .deleteCollectionForUser(this._userRef, {
        ...collection,
        books: collection.books.map(book => book.id),
      })
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `<strong>${collection.title}</strong> succesfully deleted`,
          })
        )
      )
  }

  addBooksToCollection(collection, books) {
    return this.database
      .addBooksToCollection(collection.id, books.map(book => book.id))
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `${books.length} books succesfully added to ${collection.title}`,
          })
        )
      )
  }

  removeBooksFromCollection(collection, books) {
    return this.database
      .removeBooksFromCollection(collection.id, books.map(book => book.id))
      .then(
        ignoreReturnFor(() =>
          notify({
            message: `${books.length} books succesfully removed from ${collection.title}`,
          })
        )
      )
  }
}

import { Injectable } from '@angular/core'
import { User } from 'models/user.model'
import { BehaviorSubject } from 'rxjs'

@Injectable()
export class SessionService {
  private _localUser = new BehaviorSubject<User>(undefined)
  private _userId = new BehaviorSubject<string>(undefined)
  private _goodreadsId = new BehaviorSubject<number>(undefined)

  localUser$ = this._localUser.asObservable()
  userId$ = this._userId.asObservable()
  goodreadsId$ = this._goodreadsId.asObservable()

  get localUser(): User {
    return JSON.parse(localStorage.getItem('user'))
  }

  set localUser(user: User) {
    localStorage.setItem('user', JSON.stringify(user))
    this._localUser.next(user)
    this._userId.next(user.id)
  }

  get userId() {
    return JSON.parse(localStorage.getItem('user')).id
  }

  set goodreadsId(id: number) {
    this.localUser = { ...this.localUser, goodreadsId: id }
    this._goodreadsId.next(id)
  }

  buildSession(user: User = null) {
    if (user) {
      this.localUser = user
    } else {
      user = this.localUser
    }

    console.log('Building session...')
    this._userId.next(user.id)
    this._localUser.next(user)
    this._goodreadsId.next(+user.goodreadsId)
  }

  destroySession() {
    console.log('Destroying session...')
    localStorage.removeItem('user')
    this._localUser.next(undefined)
    this._userId.next(undefined)
  }
}

