(array: any[], filter: any[], param: string) => {
  return filter ? array.filter(item => filter.includes(item[param])) : array
}

(object: object) => {
  if (!object) {
    return
  }
  return Object.keys(object).map(key => object[key])
}

(object: object) => {
  if (!object) {
    return
  }
  return Object.keys(object).map(key => ({ ...object[key], ref: key }))
}

book => {
  if (book.authors) {
    const id = book.authors.author.id || book.authors.author[0].id
    return +id
  } else {
    return +book.author.id._
  }
}

book => {
  if (book.authors) {
    if (Array.isArray(book.authors.author)) {
      return book.authors.author[0].name
    } else {
      return book.authors.author.name
    }
  }

  return book.author.name
}

book => {
  if (book.work) {
    if (book.work.id._) {
      return +book.work.id._
    } else {
      return +book.work.id
    }
  } else {
    return +book.id._
  }
}

(error): string => {
  switch (error.code) {
    case 'auth/invalid-email':
      return 'Please provide a valid e-mail'
    case 'auth/user-not-found':
      return 'User not found'
    case 'auth/wrong-password':
      return 'Wrong password'
    default:
      return 'Could not login. Try again.'
  }
}

(location: string, wait: number): void => {
  const element = document.querySelector('#' + location)
  if (element) {
    setTimeout(() => {
      element.scrollIntoView({
        behavior: 'smooth',
        block: 'start',
        inline: 'nearest',
      })
    }, wait)
  }
}

src/utils/validators.ts.defaultValue:
(control: AbstractControl, fieldName: string) => {
  const field = control.get(fieldName).value
  const fieldConfirm = control.get(`confirm${upperCaseFirstLetter(fieldName)}`)
    .value

  if (field !== fieldConfirm) {
    control
      .get(`confirm${upperCaseFirstLetter(fieldName)}`)
      .setErrors({ fieldMismatch: true })
    return { fieldMismatch: true }
  }

  return null
}

